    implementation("com.fasterxml.jackson.core:jackson-databind:2.0.1")
    
    implementation("org.eclipse.basyx:basyx.sdk:1.4.0")
    implementation("org.eclipse.basyx:basyx.components.registry:1.4.0")
    implementation("org.eclipse.basyx:basyx.components.AASServer:1.4.0")

    implementation("com.jcraft:jsch:0.1.55")
    implementation("org.apache.httpcomponents:fluent-hc:4.5.14")
}

application {
    // Define the main class for the application.
    mainClass.set("ovs_aas.App")
