    setZip64(true)
    manifest.attributes["Main-Class"] = application.mainClass
    archiveFileName.set("OVS_AAS.jar")
    destinationDirectory.set(file("/home/riccardo/Desktop/Kathara/KatharaAAS/aas_project"))
